import { chromium } from 'playwright';

const browser = await chromium.launch\(\);
const page = await browser.newPage\(\);
await page.setViewportSize\({ width: 1440, height: 860 }\);
await page.goto\('http://localhost:4321/en', { waitUntil: 'networkidle' }\);

const sections = [
  { name: '1_hero',       y: 0    },
  { name: '2_services',   y: 860  },
  { name: '3_howitworks', y: 1900 },
  { name: '4_reviews',    y: 2800 },
  { name: '5_pricing',    y: 3700 },
  { name: '6_map',        y: 4600 },
  { name: '7_form',       y: 5400 },
];

for \(const s of sections\) {
  await page.evaluate\(y => window.scrollTo\(0, y\), s.y\);
  await page.waitForTimeout\(400\);
  await page.screenshot\({ path: `/tmp/${s.name}.png` }\);
  console.log\('captured:', s.name\);
}

// Services page
await page.goto\('http://localhost:4321/en/services', { waitUntil: 'networkidle' }\);
await page.screenshot\({ path: '/tmp/8_services_page.png' }\);
console.log\('captured: services page'\);

await browser.close\(\);
EOF
node /tmp/shots.mjs 2>&1)

